mapper.select:
SELECT
    comment_no, member_no, post_no, text, delete_yn, creatdt
FROM instagram.dbo.comment WHERE post_no = ${postNO}

mapper.update:
UPDATE instagram.dbo.comment
    SET text = #{text}
	WHERE comment_no = ${commentNo}

UPDATE instagram.dbo.comment
    SET delete_yn = 'Y'
	WHERE comment_no = ${commentNo}
