mapper.update:
UPDATE instagram.dbo.member
SET login_name = #{login_name}, user_name = #{user_name}, email = #{email},
	password = #{password}, gender = #{gender}, media_id = #{media_id}
WHERE member_id = #{member_id}

UPDATE insgram.dbo.member
SET password = #{password}
WHERE member_id = #{member_id}

mapper.select:
SELECT *
FROM instagram.dbo.member
WHERE email=#{email} and password=#{password}
