mapper.update:
UPDATE instagram.dbo.member
SET login_name = #{loginName}, user_name = #{userName}, email = #{email},
	password = #{password}, gender = #{gender}, media_id = #{mediaId}
WHERE member_id = #{memberId}

UPDATE insgram.dbo.member
SET password = #{password}
WHERE member_id = #{memberId}

mapper.select:
SELECT *
FROM instagram.dbo.member
WHERE email=#{email} and password=#{password}
